export const renderTemplate = (template, model, component) => {
    const childView = component.viewContainerRef.createEmbeddedView(template, {
        $implicit: model.item,
        index: model.index,
    });
    const container = model.container.get ? model.container.get(0) : model.container;
    if (container) {
        childView.rootNodes.forEach((element) => {
            component.renderer.appendChild(container, element);
        });
    }
};
// eslint-disable-next-line @typescript-eslint/no-unused-vars
export const hasTemplate = (name, props, _component) => !!props[name];
